#version 330 core
vec4 texture2D(sampler2D s,vec2 uv) {
    return texture(s,uv);
}
uniform float time;
in vec2 vi1;
in vec2 vi2;
smooth out vec2 vo1;
mat4 rotMatrixZ(float z0) {
    return mat4 (vec4 (cos (z0),sin (z0),0.0,0.0)
                ,vec4 ((0.0) - (sin (z0)),cos (z0),0.0,0.0)
                ,vec4 (0.0,0.0,1.0,0.0)
                ,vec4 (0.0,0.0,0.0,1.0));
}
void main() {
    gl_Position = (rotMatrixZ (time)) * (vec4
        ((vi1).x,(vi1).y,-1.0,1.0));
    vo1 = vi2;
}
